import { useEffect, useState } from "react";
import "../index.css";
import "../styles/AnimeMap.css";
import TargetCore from "./TargetCore.jsx";
import TargetList from "./TargetList.jsx";
function AnimeMap() {
  //USE JSX TO CONDITIONALLY RENDER THE TAGETCORE AND BOX
  //ADD THE IMAGES INTO THE BOX, USESTATE UPDATE THE PICTURES LATER
  const [menu, setMenu] = useState(false);
  const [xPosition, setXPosition] = useState(0);
  const [yPosition, setYPosition] = useState(0);
  const [targetList, setTargetList] = useState(null);
  useEffect(() => {
    fetchTargetImages();
  }, [menu]);

  function fetchTargetImages() {
    fetch("http://localhost:3000/fetchTargetImages")
      .then((res) => {
        return res.json();
      })
      .then((data) => setTargetList(data));
  }

  function handleGame(event) {
    setMenu(!menu);
    handlePosition(event);
  }

  const handlePosition = (e) => {
    //-20 is for centering the TargetCore on the cursor
    let xPositionn = e.clientX + window.scrollX - 20;
    let yPositionn = e.clientY + window.scrollY - 20;
    setXPosition(xPositionn);
    setYPosition(yPositionn);
  };

  return (
    <>
      <img
        src="./images/midnight.png"
        alt=""
        className="Anime-Map"
        onClick={handleGame}
      ></img>
      {menu && <TargetCore x={xPosition} y={yPosition} />}
      {menu && <TargetList />}
    </>
  );
}

export default AnimeMap;
