import { useContext, useEffect, useState } from "react";
import "../index.css";
import "../styles/TargetStatus.css";
import { targetContext } from "./Midnight";

function TargetStatus() {
  const { targetStat, setTargetStatus } = useContext(targetContext);
  const { targetFound, setTargetFound } = useState(false);

  useEffect(() => {
    if (targetStat) {
      setTargetFound(true);
    } else {
      setTargetFound(false);
    }
  }, [targetStat]);

  useEffect(() => {
    setTimeout(() => {
      setTargetStatus(null);
    }, 3000);
  }, []);

  return (
    <div className="target-status">
      {targetStat && console.log(targetStat)}
      <p
        className={
          "target-status " + (targetFound ? "target-found" : "target-missed")
        }
      >
        {targetStat}
      </p>
    </div>
  );
}

export default TargetStatus;
