import { useEffect, useState } from "react";
import "../index.css";
import "../styles/AnimeMap.css";
function AnimeMap() {
  const [menu, setMenu] = useState(false);

  useEffect(() => {
    handleGame();
  }, []);

  function handleGame() {
    setMenu(!menu);
    checkTarget();
    displayMenu();
  }

  function checkTarget() {
    fetch("/getTarget", { method: "POST" })
      .then((res) => {
        console.log(res);
        return res.json();
      })
      .then((data) => {
        console.log(data);
      });
  }

  function displayMenu() {
    let root = document.querySelector("#root");
    let existingTargetBox = document.querySelector(".target-box");
    let existingTargetBoxInfo = document.querySelector(".box");

    if (menu) {
      if (!existingTargetBox) {
        let cordX = getXPosition();
        let cordY = getYPosition();
        let newTargetBox = document.createElement("div");
        newTargetBox.classList.add("target-box");
        newTargetBox.style.left = `${cordX - 50}px`;
        newTargetBox.style.top = `${cordY - 50}px`;
        newTargetBox.textContent = "+";

        let newTargetBoxInfo = document.createElement("div");
        newTargetBoxInfo.classList.add("box");
        newTargetBoxInfo.style.left = `${cordX + 10}px`;
        newTargetBoxInfo.style.top = `${cordY}px`;

        root.appendChild(newTargetBox);
        root.appendChild(newTargetBoxInfo);
      }
    } else {
      if (existingTargetBox) {
        root.removeChild(existingTargetBox);
        root.removeChild(existingTargetBoxInfo);
      }
    }
  }

  function getXPosition() {
    let xPosition = window.innerWidth / 2; // Example X Position
    console.log(xPosition, "X");
    return xPosition;
  }

  function getYPosition() {
    let yPosition = window.innerHeight / 2; // Example Y Position
    console.log(yPosition, "Y");
    return yPosition;
  }

  return (
    <>
      <img
        src="./images/Anime.jpg"
        alt=""
        className="Anime-Map"
        onClick={handleGame}
      ></img>
    </>
  );
}

export default AnimeMap;
