import { useEffect, useState } from "react";
import "../index.css";
import "../styles/AnimeMap.css";
function AnimeMap() {
  const [menu, setMenu] = useState(false);
  const [targetImages, setTargetImages] = useState(null);
  useEffect(() => {
    handleGame();
    fetchTargetImages();
  }, []);

  function fetchTargetImages() {
    fetch("http://localhost:3000/fetchTargetImages")
      .then((res) => {
        return res.json();
      })
      .then((data) => setTargetImages(data));
  }

  function handleGame(event) {
    setMenu(!menu);
    displayMenu(event);
    checkTarget();
  }

  function checkTarget() {}

  function displayMenu(event) {
    let root = document.querySelector("#root");
    let targetBox = document.querySelector(".target-box");
    let targetBoxInfo = document.querySelector(".box");

    if (menu) {
      if (!targetBox) {
        let cordX = getXPosition(event);
        let cordY = getYPosition(event);
        let targetBox = document.createElement("div");
        targetBox.classList.add("target-box");
        targetBox.style.left = `${cordX - 50}px`;
        targetBox.style.top = `${cordY - 50}px`;
        targetBox.textContent = "+";
        let targetBoxInfo = document.createElement("div");
        targetBoxInfo.classList.add("box");
        targetBoxInfo.style.left = `${cordX + 10}px`;
        targetBoxInfo.style.top = `${cordY}px`;
        root.appendChild(targetBox);
        root.appendChild(targetBoxInfo);
      }
    } else {
      if (targetBox) {
        root.removeChild(targetBox);
        root.removeChild(targetBoxInfo);
      }
    }
  }

  function getXPosition(e) {
    let xPosition = e.clientX + window.scrollX;
    console.log(xPosition, "X");
    return xPosition;
  }
  function getYPosition(e) {
    let yPosition = e.clientY + window.scrollY;
    console.log(yPosition, "Y");
    return yPosition;
  }

  return (
    <>
      <img
        src="./images/midnight.png"
        alt=""
        className="Anime-Map"
        onClick={handleGame}
      ></img>
    </>
  );
}

export default AnimeMap;
