import { useContext, useEffect, useState } from "react";
import "../index.css";
import "../styles/TargetStatus.css";
import { targetContext } from "./Midnight";

function TargetStatus() {
  const { targets, setTargets } = useContext(targetContext);

  useEffect(() => {
    setTimeout(() => {
      setTargets(null);
    }, 3000);
  }, [targets]);

  return (
    <div className="target-status">
      <p
        className={
          "target-status " +
          (targets === "Try Again" ? "target-missed" : "target-found")
        }
      >
        {targets}
      </p>
    </div>
  );
}

export default TargetStatus;
