import { useEffect, useContext } from "react";
import "../index.css";
import "../styles/SubmitData.css";
import { targetContext } from "./Midnight";

function SubmitData() {
  const {
    targetData,
    scoreboard,
    setScoreboard,
    menu,
    time,
    setIntervalId,
    intervalId,
    updateTimer,
  } = useContext(targetContext);

  function handleScoreboarrd() {
    pauseOrResume();
  }

  const pauseOrResume = () => {
    if (!intervalId) {
      let id = setInterval(updateTimer, 1000);
      setIntervalId(id);
    } else {
      clearInterval(intervalId);
      setIntervalId("");
    }
  };
  return (
    <div className="submit-container">
      <h1>You got</h1>
      <h2>{`${time.hr < 10 ? 0 : ""}${time.hr} : ${time.min < 10 ? 0 : ""}${
        time.min
      } : ${time.sec < 10 ? 0 : ""}${time.sec}`}</h2>
      <button onClick={handleScoreboarrd}></button>
    </div>
  );
}

export default SubmitData;
