import { useEffect, useState } from "react";
import "../index.css";
import "../styles/AnimeMap.css";
function AnimeMap() {
 const [menu,setMenu] = useState(false)

 useEffect(()=>{
  menuPopup()
 },[menu])

  function eventListener(){
      setMenu((prevMenu) => !prevMenu);
  }
  async function menuPopup(){
    let root = document.querySelector("#root");
    if(menu){
      
    let targetBox = document.createElement("div");
   targetBox.classList.add("target-box");
   targetBox.style.top = "300px";
   targetBox.style.left = "800px";
   root.appendChild(targetBox)
    }else{
      let targetBox = document.querySelector(".target-box")
      root.removeChild(targetBox)
    }
 
  } 

  return (
    <> 
  <img src="./images/Anime.jpg" alt="" className="Anime-Map" onClick={eventListener}></img>
  </>
  );
}

export default AnimeMap;
