import { useEffect, useState } from "react";
import "../index.css";
import "../styles/AnimeMap.css";
import TargetCore from "./TargetCore.jsx";
function AnimeMap() {
  //USE JSX TO CONDITIONALLY RENDER THE TAGETCORE AND BOX
  //ADD THE IMAGES INTO THE BOX, USESTATE UPDATE THE PICTURES LATER
  const [menu, setMenu] = useState(false);
  const [xPosition, setXPosition] = useState(0);
  const [yPosition, setYPosition] = useState(0);
  const [targetImages, setTargetImages] = useState(null);
  useEffect(() => {
    fetchTargetImages();
  }, [menu]);

  function fetchTargetImages() {
    fetch("http://localhost:3000/fetchTargetImages")
      .then((res) => {
        return res.json();
      })
      .then((data) => setTargetImages(data));
  }

  function handleGame(event) {
    setMenu(!menu);
    handleXPosition(event);
    handleYPosition(event);
  }

  const handleXPosition = (e) => {
    let xPosition = e.clientX + window.scrollX;
    console.log(xPosition, "X");
    setXPosition(xPosition);
  };
  function handleYPosition(e) {
    let yPosition = e.clientY + window.scrollY;
    console.log(yPosition, "Y");
    setYPosition(yPosition);
  }

  return (
    <>
      <img
        src="./images/midnight.png"
        alt=""
        className="Anime-Map"
        onClick={handleGame}
      ></img>
      {menu && <TargetCore x={xPosition} y={yPosition} />}
    </>
  );
}

export default AnimeMap;
