import { useContext, useEffect } from "react";
import "../index.css";
import "../styles/TargetStatus.css";
import { targetContext } from "./Midnight";

function TargetStatus() {
  const { targetStat, setTargetStatus } = useContext(targetContext);
  useEffect(() => {
    // Reset targetStat to null after 3000ms
    const timer = setTimeout(() => {
      setTargetStatus(null);
    }, 3000);

    // Cleanup function to clear the timer
    return () => clearTimeout(timer);
  }, [targetStat, setTargetStatus]);

  return (
    <div className="target-status">
      {/* Log the targetStat if it exists */}
      {targetStat && console.log(targetStat)}

      {/* Render different messages based on targetStat */}
      {targetStat !== null ? (
        <p className={targetStat ? "target-found" : "target-missed"}>
          {targetStat}
        </p>
      ) : null}
    </div>
  );
}

export default TargetStatus;
