import { useEffect, useState } from "react";
import "../index.css";
import "../styles/AnimeMap.css";
function AnimeMap() {
  const [menu, setMenu] = useState(false);

  function eventListener() {
    setMenu(!menu);
    menuPopup(); // Call menuPopup directly when menu changes
  }

  async function menuPopup() {
    if (menu) {
      const root = document.querySelector("#root");
      let targetBox = document.createElement("div");
      targetBox.classList.add("target-box");
      targetBox.style.top = "300px";
      targetBox.style.left = "800px";
      root.appendChild(targetBox);
    }
  }

  return (
    <img
      src="./images/Anime.jpg"
      alt=""
      className="Anime-Map"
      onClick={eventListener}
    />
  );
}

export default AnimeMap;
