import { useEffect, useContext } from "react";
import "../index.css";
import "../styles/SubmitData.css";
import { targetContext } from "./Midnight";
 

function SubmitData() {
  const {
    targetData,
    scoreboard,
    setScoreboard,
    menu,
    time,
    setIntervalId,
    intervalId,
    updateTimer,
  } = useContext(targetContext);

  useEffect(() => {
    pauseOrResume();
  }, []);

  function handleScoreboard() {
    const usernameInput = document.querySelector(".submit-input").value;
    fetch("http://localhost:3000/Map1Score", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: usernameInput,
        seconds: time.sec,
        minutes: time.min,
        hours: time.hr,
      }),
    });
  }

  function pauseOrResume() {
    clearInterval(intervalId);
    setIntervalId("");
  }
  return (
    <div className="submit-container">
      <h1>You got</h1>
      <h2>{`${time.hr < 10 ? 0 : ""}${time.hr} : ${time.min < 10 ? 0 : ""}${
        time.min
      } : ${time.sec < 10 ? 0 : ""}${time.sec}`}</h2>
      <h3>Enter Name</h3>
      <input type="text" className="submit-input" name="username"></input>
      <button className="submit-button" onClick={handleScoreboard}>
        Submit
      </button>
    </div>
  );
}

export default SubmitData;
